SELECT nom, lot.dateexpiration, quantite FROM lot JOIN produit ON lot.codeproduit = produit.code WHERE lot.dateexpiration - current_date BETWEEN 0 AND ?;
SELECT nom, lot.dateexpiration, quantite FROM lot JOIN produit ON lot.codeproduit = produit.code WHERE lot.dateexpiration - current_date < 0;
SELECT produit.nom, produitsegmentannee.cible - sum(lot.quantite) AS a_commander FROM produit JOIN lot ON produit.code = lot.codeproduit JOIN produitsegmentannee ON produit.code = produitsegmentannee.codeproduit JOIN segmentannee ON produitsegmentannee.idsegment = segmentannee.id GROUP BY produit.nom, produitsegmentannee.cible, produitsegmentannee.seuil HAVING sum(lot.quantite) <= produitsegmentannee.seuil;